{
	"id": "CreateWindowFamilyApp",
	"engine": "Autodesk.Revit+2019",
	"description": "Create Window Family app based on Revit 2019"
}

{
	"engine": "Autodesk.Revit+2019",
	"description": "Create Window Family App based on Revit 2019"
}

{
	"id": "CreateWindowFamilyActivity",


	"commandLine": [ "$(engine.path)\\\\revitcoreconsole.exe /i $(args[templateFile].path) /al $(appbundles[CreateWindowFamilyApp].path)" ],
	"parameters": {
	  "templateFile": {
		"zip": false,
		"ondemand": false,
		"verb": "get",
		"description": "Input Revit Window Family Templete",
		"required": true
	  },
	  "windowParams": {
		"zip": false,
		"ondemand": false,
		"verb": "get",
		"description": "Window Family parameters",
		"required": false,
		"localName": "WindowParams.json"
	  },
	  "resultFamily": {
		"zip": false,
		"ondemand": false,
		"verb": "put",
		"description": "new created Winodw Family",
		"required": true,
		"localName": "WindowFamily.rfa"
	  }
	},
	"engine": "Autodesk.Revit+2019",
	"appbundles": [ "{{dasNickName}}.CreateWindowFamilyApp+dev" ],
	"description": "Create Window Family Activity."
}

